package com.xzx.xzxms.service.impl;import com.xzx.xzxms.bean.InquiryPool;import com.xzx.xzxms.bean.InquiryPoolExample;import com.xzx.xzxms.dao.InquiryPoolMapper;import com.xzx.xzxms.service.IInquiryPoolService;import org.springframework.stereotype.Service;import javax.annotation.Resource;import java.util.List;@Servicepublic class InquiryPoolServiceImpl implements IInquiryPoolService {    @Resource    private InquiryPoolMapper inquiryPoolMapper;    @Override    public List<InquiryPool> findByParams(String name, String brand) {        List<InquiryPool> inquiryPools;        InquiryPoolExample example = new InquiryPoolExample();        if (name != "" && brand != ""){            example.createCriteria().andEquipmentNameEqualTo(name).andQuoteBrandEqualTo(brand);            inquiryPools = inquiryPoolMapper.selectByExample(example);        }else if(name != "" && brand == ""){            example.createCriteria().andEquipmentNameEqualTo(name);            inquiryPools = inquiryPoolMapper.selectByExample(example);        }else if (name == "" && brand != ""){            example.createCriteria().andQuoteBrandEqualTo(brand);            inquiryPools = inquiryPoolMapper.selectByExample(example);        }else {            inquiryPools = inquiryPoolMapper.selectByExample(example);        }        return inquiryPools;    }}