mapper.select:
select q.su_price, s.supplier supplier, i.model model,i.name name,i.number number,i.params params,i.unit unit,i.price price,i.total_price total_price, i.sort sort,i.veto veto,i.finally_price finally_price,i.correct_price, i.template_id template_id, spc.id compare_id, spc.technical_audit technical_audit,spc.technical_remark technical_remark, spc.business_audit business_audit,spc.business_remark business_remark, spc.compare_audit compareStatus,spc.compare_remark compare_remark, spc.finally_audit finally_audit,spc.finally_remark finally_remark from inquiry i left join sys_pro_detail pro on pro.id = i.pro_detail_id left join quote q on q.inquiry_id = i.id left join supply_supplier s on q.supplier_id = s.id left join sys_pro_check spc on q.id = spc.quote_id
pro.is_active = 1 and i.is_active= 1 and q.is_active = 1
and i.pro_detail_id = #{proDetailId,jdbcType=BIGINT}
and i.name = #{name}
order by i.sort,i.id,q.su_price
