mapper.select:
SELECT spc.*, q.id q_id,q.image q_image, ss.supplier q_supplier, q.su_delivery q_su_delivery,q.su_model q_su_model,q.su_params q_su_params,q.su_brand q_su_brand, q.su_price q_su_price,q.su_total_price q_su_total_price,q.warranty q_warranty,q.data_source q_data_source,q.su_remark q_su_remark, i.model i_model,i.name i_name,i.number i_number,i.params i_params,i.sort i_sort,i.unit i_unit,i.template_id i_template_id FROM sys_pro_check spc LEFT JOIN quote q ON spc.quote_id = q.id LEFT JOIN inquiry i on q.inquiry_id = i.id LEFT JOIN sys_pro_detail spd ON i.pro_detail_id = spd.id LEFT JOIN sys_file sf ON sf.other_id = q.id left join supply_supplier ss on ss.id = q.supplier_id
q.is_active = 1 and i.is_active = 1
AND spc.technical_audit = #{status}
AND spd.id = #{proDetailId,jdbcType=BIGINT}
order by i.name, i.model

SELECT spc.*, q.id q_id,q.image q_image, ss.supplier q_supplier, q.su_delivery q_su_delivery,q.su_model q_su_model,q.su_params q_su_params, q.su_price q_su_price,q.su_total_price q_su_total_price,q.warranty q_warranty,q.data_source q_data_source,q.su_remark q_su_remark, i.model i_model,i.name i_name,i.number i_number,i.params i_params,i.sort i_sort,i.unit i_unit,i.template_id i_template_id FROM sys_pro_check spc LEFT JOIN quote q ON spc.quote_id = q.id LEFT JOIN inquiry i on q.inquiry_id = i.id LEFT JOIN sys_pro_detail spd ON i.pro_detail_id = spd.id LEFT JOIN sys_file sf ON sf.other_id = q.id left join supply_supplier ss on ss.id = q.supplier_id
q.is_active = 1 and i.is_active = 1
AND spc.business_audit = #{status}
AND spd.id = #{proDetailId,jdbcType=BIGINT}
AND q.supplier like "%"#{supplier,jdbcType=VARCHAR}"%"
order by i.name, i.model
